const Map = () => {
  const { username } = useContext(AuthContext);
  const { socketAnswerPickRegion, socketAnswerAttackRegion, roomInfo } =
    useContext(SocketContext);

  const [hoverRegion, setHoverRegion] = useState(null);

  const isPlayerRegionNeighbor = (regionIdx) => {
    const playerRegions = [];

    for (let idx = 0; idx < NUMBER_OF_REGIONS; idx++) {
      if (roomInfo.map[idx].owner !== username) continue;
      if (playerRegions.includes(idx)) continue;

      playerRegions.push(idx);
    }

    if (playerRegions.length === 0) {
      // player has no regions
      playerRegions.push(14);
    }

    return GAME_REGION_NEIGHBORS[regionIdx].some((regIdx) =>
      playerRegions.includes(regIdx)
    );
  };

  const regionMouseEnter = (e) => {
    setHoverRegion(e.target.attributes.getNamedItem("id").value);
  };

  const regionMouseLeave = (e) => {
    setHoverRegion(null);
  };

  const nighborColor = "#9A9A9A";
  const forbiddenColor = "#505050";
  const selectedColor = "white";

  // pls don't look here it's terrible
  const fillColor = (idx) => {
    if (roomInfo.gameState === GAME_STATES.REGION_ATTACK) {
      if (roomInfo.currentAttack.attacker === username) {
        if (roomInfo.currentAttack.attacker === roomInfo.map[idx].owner)
          return PLAYER_COLORS_DARK[
            roomInfo.playerColors[roomInfo.map[idx].owner]
          ];
        if (hoverRegion === idx.toString() && isPlayerRegionNeighbor(idx))
          return PLAYER_COLORS_LIGHT[
            roomInfo.playerColors[roomInfo.map[idx].owner]
          ];
        if (isPlayerRegionNeighbor(idx))
          return PLAYER_COLORS[roomInfo.playerColors[roomInfo.map[idx].owner]];
        return PLAYER_COLORS_DARK[
          roomInfo.playerColors[roomInfo.map[idx].owner]
        ];
      }
      return PLAYER_COLORS[roomInfo.playerColors[roomInfo.map[idx].owner]];
    }

    if (roomInfo.map[idx].owner)
      return PLAYER_COLORS[roomInfo.playerColors[roomInfo.map[idx].owner]];

    if (roomInfo.gameState !== GAME_STATES.REGION_PICK) return forbiddenColor;
    if (roomInfo.currentPick.username !== username) return forbiddenColor;
    if (
      !isPlayerRegionNeighbor(idx) &&
      roomInfo.currentPick.onlyNeighbors === true
    )
      return forbiddenColor;

    if (hoverRegion === idx.toString()) return selectedColor;

    return nighborColor;
  };

  const handleClickRegion = (e) => {
    e.preventDefault();

    const regionIdx = e.target.attributes.getNamedItem("id").value;

    switch (roomInfo.gameStage) {
      case GAME_STAGES.TAKE_REGIONS:
        if (roomInfo.gameState !== GAME_STATES.REGION_PICK) return;
        if (roomInfo.map[regionIdx].owner !== null) return;
        if (roomInfo.currentPick.username !== username) return;
        if (
          !isPlayerRegionNeighbor(regionIdx) &&
          roomInfo.currentPick.onlyNeighbors === true
        )
          return;

        socketAnswerPickRegion(regionIdx);
        return;

      case GAME_STAGES.BATTLE_REGIONS:
        if (roomInfo.gameState !== GAME_STATES.REGION_ATTACK) return;
        if (roomInfo.currentAttack.attacker !== username) return;
        if (roomInfo.map[regionIdx].owner === username) return;
        if (!isPlayerRegionNeighbor(regionIdx)) return;

        socketAnswerAttackRegion(regionIdx);
        return;

      default:
        return;
    }
  };

  return (
    <>
      <motion.svg
        animate={{ scale: 1 }}
        initial={{ scale: 0 }}
        transition={{ duration: 0.5 }}
        stroke="#303030"
        strokeWidth="3"
        viewBox="0 0 1000 570"
        strokeLinecap="round"
        strokeLinejoin="round"
        strokeOpacity="0.7"
      >
        {regions.map((region, idx) => {
          return (
            <motion.path
              className={`${
                fillColor(idx) === selectedColor ||
                PLAYER_COLORS_LIGHT.includes(fillColor(idx))
                  ? "cursor-pointer"
                  : null
              }`}
              animate={{ scale: idx === 10 ? 1.4 : 1 }}
              initial={{ scale: 0 }}
              transition={{
                duration: 0.5,
                delay:
                  roomInfo.map[idx].owner &&
                  roomInfo.gameState !== GAME_STATES.START
                    ? 0
                    : 0.5 + idx * 0.1,
              }}
              key={
                roomInfo.map[idx].owner ? roomInfo.map[idx].owner + idx : idx
              }
              id={idx}
              d={region.path}
              name={region.name}
              onMouseEnter={regionMouseEnter}
              onClick={handleClickRegion}
              onMouseLeave={regionMouseLeave}
              fill={fillColor(idx)}
              style={{
                transform: idx === 10 ? "translate(-10%, -11.3%)" : null,
                zIndex: idx === 5 ? 1 : null,
              }}
              strokeWidth={idx === 10 ? "2.2" : null}
            />
          );
        })}
        {roomInfo.currentAttack && roomInfo.currentAttack.region !== null ? (
          <g
            transform={`translate(${
              regions[roomInfo.currentAttack.region].ax
            }, ${
              regions[roomInfo.currentAttack.region].ay
            }) scale(0.007, -0.007)`}
            strokeWidth="340"
            stroke="black"
          >
            <motion.path
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.8 }}
              fill="#e0e0e0"
              d="M425.4,4819.4c0-12.9,36-406,77.1-876.3c87.4-956,87.4-961.1,300.7-1161.6c66.8-64.2,719.5-647.6,1446.8-1295.2C2977.2,838.8,3599.1,286.3,3632.5,255.4l56.5-56.5l-398.3-447.1L2892.4-698l557.7-557.6c308.4-308.4,567.9-560.2,578.2-560.2c10.3,0,233.8,190.2,496,426.6l475.4,424l475.4-424c259.6-236.4,483.1-426.6,493.4-426.6c12.9,0,272.4,251.8,580.8,560.2L7106.8-698L6706-248.3l-400.9,449.7L6431,309.4c66.8,59.1,742.7,660.4,1498.2,1336.3c1094.7,976.5,1387.7,1251.5,1431.4,1344c51.4,100.2,69.4,236.4,136.2,974c43.7,470.3,71.9,863.4,64.2,871.2c-7.7,7.7-403.5-20.5-881.4-64.2c-673.3-61.7-889.2-89.9-966.3-125.9c-74.5-36-439.4-429.2-1395.4-1500.8C5606.1,2344.7,5015,1689.4,5004.8,1686.8c-10.3-5.1-601.3,647.6-1313.2,1446.8c-938,1048.5-1326,1467.4-1400.5,1505.9c-84.8,43.7-246.7,64.2-899.4,125.9C525.6,4845.1,425.4,4850.2,425.4,4819.4z"
            />
            <motion.path
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.7 }}
              fill={
                PLAYER_COLORS[
                  roomInfo.playerColors[roomInfo.currentAttack.attacker]
                ]
              }
              d="M1671.7-297.1c-123.3-56.5-336.7-277.6-362.3-375.2c-7.7-36-10.3-125.9-5.1-197.9c12.9-125.9,20.6-133.6,370-485.7l357.2-357.2l-660.4-660.4l-663-663l-136.2,136.2c-107.9,105.4-156.8,136.2-221,138.8c-164.5,5.1-295.5-167-236.4-310.9c18-38.6,362.3-400.9,768.4-806.9c729.8-727.2,740.1-737.5,845.5-737.5c87.4,0,118.2,15.4,172.2,79.7c105.4,125.9,89.9,208.2-74.5,380.4l-138.8,146.5l647.6,647.6c357.2,357.2,660.4,647.6,673.3,647.6c15.4,0,169.6-146.5,346.9-323.8c177.3-177.3,352.1-339.2,390.6-359.8c82.2-43.7,221-46.3,321.2-7.7c84.8,30.8,305.8,239,357.2,336.6c46.3,89.9,46.3,285.2,0,375.2c-20.6,38.5-562.8,593.6-1202.7,1230.9c-1177,1177-1223.2,1218.1-1395.4,1215.5C1797.6-248.3,1728.3-271.4,1671.7-297.1z"
            />
            <motion.path
              initial={{ scale: 0 }}
              animate={{ scale: 1 }}
              transition={{ duration: 0.7 }}
              fill={
                PLAYER_COLORS[
                  roomInfo.playerColors[roomInfo.currentAttack.attacker]
                ]
              }
              d="M8006.3-276.5c-79.7-30.8-2377.1-2320.5-2431-2423.3c-51.4-92.5-51.4-272.4-2.6-364.9c54-102.8,272.4-311,359.8-341.8c100.2-38.5,239-36,321.2,7.7c38.5,20.6,213.3,182.5,390.6,359.8c177.3,177.3,331.5,323.8,346.9,323.8c12.8,0,316.1-290.4,673.3-647.6l647.6-647.6l-138.8-146.5c-164.5-172.2-179.9-254.4-74.5-380.4c54-64.2,84.8-79.7,172.2-79.7c105.4,0,115.6,10.3,845.5,737.5c406,406,750.4,768.4,768.4,809.5c59.1,143.9-61.7,303.2-231.3,303.2c-74.5,0-115.6-23.1-226.1-133.6l-136.2-136.2l-663,663L7967.7-1713l357.2,357.2c349.5,352.1,357.2,359.8,370,485.7c15.4,185-7.7,249.3-143.9,395.8C8368.6-276.5,8178.5-209.7,8006.3-276.5z"
            />
          </g>
        ) : null}
        {regions.map((region, idx) => {
          if (roomInfo.map[idx].owner)
            return (
              <motion.text
                className="blocker"
                animate={{ scale: 1, opacity: 1 }}
                initial={{ scale: 0, opacity: 0 }}
                transition={{
                  duration: 0.5,
                  delay:
                    roomInfo.map[idx].owner &&
                    roomInfo.gameState !== GAME_STATES.START
                      ? 0
                      : 0.5 + idx * 0.1,
                }}
                key={idx}
                name={region.name}
                x={region.x}
                y={region.y}
                fill={
                  fillColor(idx) === selectedColor ||
                  PLAYER_COLORS_LIGHT.includes(fillColor(idx))
                    ? "red"
                    : PLAYER_COLORS_DARK.includes(fillColor(idx))
                    ? "#2C2C2C"
                    : "white"
                }
                stroke="black"
                fontSize="28"
                fontWeight="bold"
                strokeWidth="1"
                pointerEvents="none"
              >
                {roomInfo.map[idx].price}
              </motion.text>
            );
          return null;
        })}
      </motion.svg>
    </>
  );
};

export default Map;
